import {AfterViewInit, Component, ElementRef, ViewChild} from '@angular/core';

@Component({
  selector: 'app-telegram-login',
  template: '<button class="telegram-login-button" data-telegram-login="SoftconBot" data-size="medium" data-userpic="false" data-request-access="write"></button>',
})
export class TelegramLoginComponent{

  onTelegramAuth(user: any) {
    alert('Logged in as ' + user.first_name + ' ' + user.last_name + ' (' + user.id + (user.username ? ', @' + user.username : '') + ')');
  }

}
