import { Component, AfterViewInit, ViewChild, ElementRef } from '@angular/core';
import { User } from 'angular-telegram-login-widget/lib/Types';

import {environment} from "../../environments/environment";

@Component({
  selector: 'app-telegram-login-widget',
  template: `
    <div #script style.display="none">
      <ng-content></ng-content>
    </div>`,
  styleUrls: ['./telegram-login-widget.component.css']
})
export class TelegramLoginWidgetComponent implements AfterViewInit {
  @ViewChild('script', { static: true }) script: ElementRef;

  constructor(private telegramLoginService: TelegramLoginService) {}

  ngAfterViewInit() {
    this.telegramLoginService.init();

    const element = this.script.nativeElement;
    const script = document.createElement('script');
    script.src = 'https://telegram.org/js/telegram-widget.js?5';
    script.setAttribute('data-telegram-login', "softcon");
    script.setAttribute('data-size', 'large');
    // Callback function in global scope
    script.setAttribute('data-onauth', 'loginViaTelegram(user)');
    script.setAttribute('data-request-access', 'write');
    element.parentElement.replaceChild(script, element);
  }
}
